        let name = event.target.name
        let value = event.target.value
        console.log(name)
        console.log(value)
        setVal((prevVal)=> {
            return{
                
            }
        })
    }
    const formSubmit  = (e) => {
        e.preventDefault()
        alert(val)
    }
    return (
        <>
            <form action="#" method="get" onSubmit={formSubmit}>
                <div>Full Name</div>
                <input name="nam" onChange={getValue} placeholder="Name" type="text" className="form-control mb-3" />
                <div>Phone</div>
                <input name="phn" onChange={getValue} placeholder="Phone Number" type="text" className="form-control mb-3" />
                <div>Email Adress</div>
                <input name="mail" onChange={getValue} placeholder="Email" type="text" className="form-control mb-3" />
                <div>Massage</div>
                <textarea name="msg" onChange={getValue} className="form-control" rows='4' />
                <button type="submit" className="btn btn-outline-primary mt-4">Submit Form</button>
            </form>
        </>
    )
};

export default MainFrom;
