import React, { useState } from "react";

const Contact = () => {
    const [val, setVal] =  useState({
        nam: '',
        phn
    })

    const getValue= (event) => {
        setVal(event.target.value)
    }
    const formSubmit  = (e) => {
        e.preventDefault()
        alert(val)
    }
    return (
        <>
            <div className="container">
                <div className="contact_head text-center"><h1>Contact US</h1></div>
                <div className="row m-auto w-50">
                    <form action="#" method="get" onSubmit={formSubmit}>
                        <div>Full Name</div>
                        <input onChange={getValue} placeholder="Name" type="text" className="form-control mb-3" />
                        <div>Phone</div>
                        <input onChange={getValue} placeholder="Phone Number" type="text" className="form-control mb-3" />
                        <div>Email Adress</div>
                        <input onChange={getValue} placeholder="Email" type="text" className="form-control mb-3" />
                        <div>Massage</div>
                        <textarea onChange={getValue} className="form-control" rows='4' />
                        <button type="submit" className="btn btn-outline-primary mt-4">Submit Form</button>
                    </form>
                </div>
            </div>
        </>
    )
};

export default Contact;
